from selenium import webdriverfrom selenium.webdriver.common.keys import Keysimport timeclass TwitterBot:	# Constructor	def __init__(self, username, password):		self.username = username		self.password = password		self.driver = webdriver.Chrome()	def CloseBrowser(self):		self.driver.close()	# Login functions	def login(self):		driver = self.driver		driver.get("https://twitter.com/")		time.sleep(2)		# Find login link		login_link = driver.find_element_by_xpath("//a[@href='/login']")		login_link.click()		time.sleep(2)		# Find login input fields		user_name = driver.find_element_by_xpath("//input[@class='js-username-field email-input js-initial-focus']")		user_name.clear()		user_name.send_keys(self.username)		pass_word = driver.find_element_by_xpath("//input[@class='js-password-field']")		pass_word.clear()		pass_word.send_keys(self.password)		pass_word.send_keys(Keys.RETURN)		time.sleep(5)tbot = TwitterBot("kodenaut", "DinosaursKill254Kids!")tbot.login()